import React, { Component } from 'react';
import './App.css';

class Course {
  constructor(college, dept, number, section, prof) {
    this.college = college;
    this.dept = dept;
    this.number = number;
    this.section = section;
    this.prof = prof;
    this.key = `${this.college} ${this.dept} ${this.number} ${this.section} ${this.prof}`;
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    const columns = [
      'College',
      'Department',
      'Number',
      'Section',
      'Professor',
    ];

    const courses = [
      new Course('CAS', 'CS', '112', 'B1', 'Sullivan'),
      new Course('CAS', 'CS', '332', 'A1', 'Bun'),
      new Course('CAS', 'CS', '350', 'A1', 'Sarkar'),
    ];

    this.state = {
      columns,
      courses,
    };
    this.addNew = this.addNew.bind(this);
  }

  render() {
    const {columns, courses} = this.state;
    return (
        <table>
          <thead>
            {columns.map(column => <th key={column}>{column}</th>)}
          </thead>
          <tbody>
            {
              courses.map(course => <tr key={course.key}>
                <td>{course.college}</td>
                <td>{course.dept}</td>
                <td>{course.number}</td>
                <td>{course.section}</td>
                <td>{course.prof}</td>
                <td>
                  <button onClick={() => this.myCourse(course.key)}>Add to my courses</button>
                </td>
              </tr>)
            }
          </tbody>
        </table>
    );
  }
}

export default App;
